import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Breadcrumb, Spinner, Row, Col } from 'react-bootstrap';
import globalVar from '../../globalVar';

const LectureDetail = () => {
    const { lectureId } = useParams(); // This is the examId
    const [lectureDetail, setLectureDetail] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchLectureDetails = async () => {
            const requestOptions = {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: new FormData(),
            };
            requestOptions.body.append("examId", lectureId); // Pass the examId

            try {
                const response = await fetch(`${globalVar.url}api/related_lectures`, requestOptions);
                if (!response.ok) throw new Error('Failed to fetch lecture details');
                const data = await response.json();
                setLectureDetail(data.data);
            } catch (err) {
                setError(err);
            } finally {
                setLoading(false);
            }
        };

        if (lectureId) {
            fetchLectureDetails();
        }
    }, [lectureId]);

    return (
        <section className="section section-lectures inner-page">
            <Container>
                {loading ? (
                    <div className="d-flex align-items-center justify-content-center" style={{ height: "250px" }}>
                        <Spinner animation="border" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </Spinner>
                    </div>
                ) : error ? (
                    <div className="alert alert-danger">Error: {error.message}</div>
                ) : (
                    <>
                        <Breadcrumb>
                            <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
                            <Breadcrumb.Item href="/courses">Courses</Breadcrumb.Item>
                            <Breadcrumb.Item active>Lecture Details</Breadcrumb.Item>
                        </Breadcrumb>

                        <Row>
                            {lectureDetail.length > 0 ? (
                                lectureDetail.map((lecture) => (
                                    <Col key={lecture.lectureId} sm={6} lg={4}>
                                        <h5>{lecture.name}</h5>
                                        <p>{lecture.description}</p>
                                    </Col>
                                ))
                            ) : (
                                <div className="alert alert-info">No related lectures found.</div>
                            )}
                        </Row>
                    </>
                )}
            </Container>
        </section>
    );
};

export default LectureDetail;
