import PropTypes from 'prop-types'
import React, { Component } from 'react'
import { Link } from "react-router-dom";
import { Container, Row, Col, Image, Button } from 'react-bootstrap';
import globalVar from '../../globalVar';

export class Certificate extends React.Component {
    static propTypes = {}

    handleCertificateClick = async (certificationId) => {
        // Log the certificateId in the console
        console.log("Clicked Certificate ID:", certificationId);
        
        // Call the API to check exam availability based on the certificationId
        const requestOptions = {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ certificationId: certificationId })
        };

        try {
            const response = await fetch(globalVar.url + 'available_exam', requestOptions);
            const data = await response.json();
            
            // Log available exams in the console
            console.log("Available Exams for Certificate ID", certificationId, ":", data);
        } catch (error) {
            console.log("Error fetching available exams:", error);
        }
    }

    render() {
        return (
            <>
                <div className="course-box" key={this.props.certificationId}>
                    {/* Call handleCertificateClick on click */}
                    <Link to={`/certificate/${this.props.certificationId}`} onClick={() => this.handleCertificateClick(this.props.certificationId)}>
                        <div className="course-thumbnail">
                            <Image 
                                className='w-100' 
                                src={'/assets/images/courses/coumbnail.jpg'} 
                                alt="certificate thumbnail"
                                onError={({ currentTarget }) => {
                                    currentTarget.onerror = null; // prevents looping
                                    currentTarget.src = "/assets/images/courses/course-thumbnail.jpg";
                                }}
                            />
                        </div>
                        {this.props.certification_Name && (
                            <div className="course-title">{this.props.certification_Name}</div>
                        )}
                    </Link>
                </div>
            </>
        )
    }
}

export default Certificate;
