import PropTypes from "prop-types";
import React from "react";
import { Container, Spinner, Breadcrumb } from "react-bootstrap";
import globalVar from "../../../globalVar";

export class LectureDetail extends React.Component {
  static propTypes = {
    examId: PropTypes.number.isRequired, // Expect examId as a prop
  };

  constructor(props) {
    super(props);
    this.state = {
      lectureDetails: [],
      showLoader: true,
    };
  }

  componentDidMount() {
    const { examId } = this.props;
    this.fetchLectureDetails(examId);
  }

  fetchLectureDetails = async (examId) => {
    const formData = new FormData();
    formData.append("examId", examId);

    const requestOptions = {
      method: "POST",
      body: formData,
    };

    try {
      const response = await fetch(`${globalVar.url}related_lectures`, requestOptions);
      if (!response.ok) {
        throw new Error("Failed to fetch lecture details");
      }
      const data = await response.json();
      this.setState({ lectureDetails: data, showLoader: false });
      console.log("Lecture Details:", data);
    } catch (error) {
      console.error("Error fetching lecture details:", error);
      this.setState({ showLoader: false });
    }
  };

  render() {
    const { lectureDetails, showLoader } = this.state;

    return (
      <section className="section section-lecture-detail inner-page">
        <Container>
          {showLoader ? (
            <div className="d-flex align-items-center justify-content-center" style={{ height: "250px" }}>
              <Spinner animation="border" role="status">
                <span className="visually-hidden">Loading...</span>
              </Spinner>
            </div>
          ) : (
            <>
              <Breadcrumb>
                <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
                <Breadcrumb.Item href="/courses">Courses</Breadcrumb.Item>
                <Breadcrumb.Item active>Lecture Details</Breadcrumb.Item>
              </Breadcrumb>

              <div className="lecture-details">
                {lectureDetails && lectureDetails.length > 0 ? (
                  lectureDetails.map((lecture, index) => (
                    <div key={index} className="lecture-detail-item">
                      <h3>{lecture.lecture_title}</h3>
                      <p>{lecture.lecture_description}</p>
                    </div>
                  ))
                ) : (
                  <p>No lectures found for this exam.</p>
                )}
              </div>
            </>
          )}
        </Container>
      </section>
    );
  }
}

export default LectureDetail;
