import PropTypes from "prop-types";
import React from "react";
import { Link } from "react-router-dom";
import { Image } from "react-bootstrap";
import globalVar from "../../../globalVar";

export class LectureDetails extends React.Component {
    static propTypes = {
        lectureId: PropTypes.number.isRequired,
        lectureTitle: PropTypes.string.isRequired,
        courseId: PropTypes.number.isRequired,
        lectureDetail: PropTypes.string, // Optional
        status: PropTypes.string, // Optional
    };

    handleLectureClick = async (lectureId, courseId) => {
        console.log("Clicked Lecture ID:", lectureId);
        console.log("Course ID:", courseId); // Log courseId to the console

        const formData = new FormData();
        formData.append("lectureId", lectureId);

        const requestOptions = {
            method: "POST",
            body: formData,
        };

        try {
            const response = await fetch(
                `${globalVar.url}api/related_lectures`,
                requestOptions
            );
            if (!response.ok) throw new Error("Failed to fetch lecture details");

            const data = await response.json();
            console.log(
                "Lecture Details for Lecture ID",
                lectureId,
                ":",
                data
            );
        } catch (error) {
            console.error("Error fetching lecture details:", error);
        }
    };

    render() {
        const { lectureId, lectureTitle, lectureDetail, courseId, status } = this.props;

        return (
            <div className="lecture-box" key={lectureId}>
                <Link
                    to={`/lecture-details/${lectureId}?courseId=${courseId}&lectureTitle=${encodeURIComponent(lectureTitle)}&lectureDetail=${encodeURIComponent(lectureDetail || '')}&status=${encodeURIComponent(status || '')}`}
                    onClick={() => this.handleLectureClick(lectureId, courseId)}
                >
                    <div className="lecture-thumbnail">
                        <Image
                            className="w-100"
                            src={"/assets/images/lectures/thumbnail.jpg"}
                            alt="lecture thumbnail"
                            onError={({ currentTarget }) => {
                                currentTarget.onerror = null;
                                currentTarget.src =
                                    "/assets/images/lectures/default-thumbnail.jpg";
                            }}
                        />
                    </div>
                    {lectureTitle && (
                        <div className="lecture-title">{lectureTitle}</div>
                    )}
                </Link>
            </div>
        );
    }
}

export default LectureDetails;
