import React, { useState, useEffect } from "react";
import { Container, Row, Col, Breadcrumb, Spinner, Card } from "react-bootstrap";
import { useParams, useLocation } from "react-router-dom";
import globalVar from "../globalVar";

const ExamAvailability = () => {
  // Extract certificationId from route params and vendorId from the URL query
  const { certificationId } = useParams();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const vendorId = queryParams.get("vendorId");
  
  // Log the vendorId and certificationId to the console
  useEffect(() => {
    console.log("Certification ID:", certificationId);
    console.log("Vendor ID:", vendorId);
  }, [certificationId, vendorId]);

  // State to manage exams, certificate details, and loading/error states
  const [exams, setExams] = useState([]);
  const [certificateDetail, setCertificateDetail] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const requestOptions = {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ certificationId, vendorId }), // Pass both ids in the request body
      };

      try {
        // Fetch certificate details
        const certificateResponse = await fetch(
          globalVar.url + "certificates",
          requestOptions
        );
        if (!certificateResponse.ok)
          throw new Error("Certificate details fetch failed");
        const certificateData = await certificateResponse.json();
        setCertificateDetail(certificateData.data); // Update certificate detail state

        // Fetch exams data
        const examsResponse = await fetch(
          globalVar.url + "available_exam",
          requestOptions
        );
        if (!examsResponse.ok) throw new Error("Exams fetch failed");
        const examsData = await examsResponse.json();
        setExams(examsData.data); // Update exams state
      } catch (error) {
        setError(error); // Handle fetch errors
      } finally {
        setLoading(false);
      }
    };

    if (certificationId && vendorId) {
      fetchData(); // Trigger data fetch only if certificationId and vendorId are available
    } else {
      setError(new Error("Missing certificationId or vendorId"));
      setLoading(false);
    }
  }, [certificationId, vendorId]);

  return (
    <section className="section section-exams inner-page">
      <Container>
        {loading ? (
          <div
            className="d-flex align-items-center justify-content-center"
            style={{ height: "250px" }}
          >
            <Spinner animation="border" role="status">
              <span className="visually-hidden">Loading...</span>
            </Spinner>
          </div>
        ) : error ? (
          <div className="alert alert-danger">
            Error fetching data: {error.message}
          </div>
        ) : (
          <>
            <Breadcrumb>
              <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
              <Breadcrumb.Item href="/certificates">
                Certificates
              </Breadcrumb.Item>
              <Breadcrumb.Item active>Exam Availability</Breadcrumb.Item>
            </Breadcrumb>

            <div className="mb-4">
              {certificateDetail ? (
                <>
                  <h2 className="title page-title d-flex align-items-center">
                    <span>{certificateDetail.certification_Name}</span>
                    <span className="d-inline-flex ms-3">
                      <span className="certificateStatus-tag">
                        {certificateDetail.status || "Completed"} {/* Default status */}
                      </span>
                    </span>
                  </h2>
                  <p>{certificateDetail.detail}</p> {/* Certificate details */}
                </>
              ) : (
                <div className="alert alert-info">
                  No certificate details available.
                </div>
              )}
            </div>

            {/* List of exams */}
            <Row>
              {exams.length > 0 ? (
                exams.map((exam) => (
                  <Col sm={6} lg={4} xl={3} key={exam.examId}>
                    <Card className="">
                      <div className="course-thumbnail">
                        <Card.Img
                          className="w-100"
                          src={"/assets/images/courses/course-thumbnail.jpg"}
                          alt="certificate thumbnail"
                          onError={({ currentTarget }) => {
                            currentTarget.onerror = null;
                            currentTarget.src =
                              "/assets/images/courses/course-thumbnail.jpg";
                          }}
                        />
                      </div>
                    </Card>
                    <h5 className="card-title course-title mb-3 mt-1 fw-bold">
                      {exam.exam_Name}
                    </h5>
                  </Col>
                ))
              ) : (
                <Col>
                  <div className="alert alert-info">
                    No exams available for this certificate.
                  </div>
                </Col>
              )}
            </Row>
          </>
        )}
      </Container>
    </section>
  );
};

export default ExamAvailability;
